document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".dropdown-toggle");e.addEventListener("click",t=>{t.preventDefault();const n=e.getAttribute("aria-expanded")==="true";e.setAttribute("aria-expanded",!n)});const t=document.querySelectorAll(".dropdown-sub-toggle");t.forEach(e=>{e.addEventListener("click",n=>{n.preventDefault();const s=e.getAttribute("aria-expanded")==="true";e.setAttribute("aria-expanded",!s),t.forEach(t=>{t!==e&&t.getAttribute("aria-expanded")==="true"&&t.setAttribute("aria-expanded","false")})})}),document.addEventListener("click",n=>{n.target.closest(".dropdown")||(e.setAttribute("aria-expanded","false"),t.forEach(e=>{e.setAttribute("aria-expanded","false")}))})})